  `last_login_time` datetime(0) NULL DEFAULT NULL COMMENT '最后登陆时间',
  `del_flag` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '删除标记：Y-已删除，N-未删除',
